import React from 'react';
import logo from './logo.svg';
import './App.css';



class FeedUrlForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      feedUrl: '',
    };

    this.handleChange = this.handleChange.bind(this);

  }



  handleChange(event) {
    this.setState({feedUrl: event.target.value});
  }

  // Pop the state up into the FeedBuilder class and do all that nonsense

  // And pop the handleClick up as well

  render() {
    return (
      <div>
        <form onSubmit={this.props.handleSubmit}>
        <label>
          Enter feed url:
          <input type="text" value={this.state.feedUrl} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>

    );
  }
}


class FeedBuilder extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      feed_title: '',
      feed_url: '',
    }

    this.handleSubmit = this.handleSubmit.bind(this);
  };

  handleSubmit(event) {
    console.log(this.state.feedUrl);
    event.preventDefault();
    console.log(encodeURIComponent(this.state.feedUrl));

    /*fetch('/express_backend').then((res) =>{
      console.log(res.json());
    }); */

    fetch('/fetch_feed/' + encodeURIComponent(this.state.feedUrl), {
      method: 'GET',
    }).then((res) => {
      res.text().then((xmlTxt) => {
        console.log(xmlTxt);
      })
    }).catch(() => console.error('Error in fetching the RSS feed')) 
  } 

  render() {
    return (
      <FeedUrlForm onSubmit={this.handleSubmit}/>
    );
  }
}


function App() {


  return (
    <FeedBuilder />

  );
}

export default App;
