import React from 'react';
import logo from './logo.svg';
import './App.css';



class FeedUrlForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      feedUrl: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    console.log(this.state.feedUrl);
    event.preventDefault();
    console.log(encodeURIComponent(this.state.feedUrl));

    /*fetch('/express_backend').then((res) =>{
      console.log(res.json());
    }); */

    fetch('/fetch_feed/' + encodeURIComponent(this.state.feedUrl), {
      method: 'GET',
    }).then((res) => {
      res.text().then((xmlTxt) => {
        console.log(res);
        console.log("meow" + xmlTxt);
      })
    }).catch(() => console.error('Error in fetching the RSS feed')) 
  } 

  handleChange(event) {
    this.setState({feedUrl: event.target.value});
  }


  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
        <label>
          Enter feed url:
          <input type="text" value={this.state.feedUrl} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>

    );
  }
}


function App() {
  return (
    <FeedUrlForm />

  );
}

export default App;
